import { BrowserRouter, NavLink, Route, Switch } from 'react-router-dom';
import styled from 'styled-components';
import TaskContext from './contexts/task-store';
import useLocalStorage from './hooks/use-local-storage';
import FocusScreen from './screens/FocusScreen';
import ListScreen from './screens/ListScreen';
import { colors, GlobalStyle } from './styles';
import { Task } from './types';
import './app.css';
import {useState} from 'react';


const Layout = styled.div`
  align-items: center;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  padding: 35px;
`;

const Nav = styled.nav`
  display: flex;
  margin-bottom: 45px;
`;

const TabButton = styled(NavLink)`
  align-items: center;
  background: #000;
  color: #fff;
  display: flex;
  height: 62px;
  justify-content: center;
  text-decoration: none;
  width: 120px;

  &:first-child {
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
  }

  &:last-child {
    border-top-right-radius: 15px;
    border-bottom-right-radius: 15px;
  }

  &.active {
    background: ${colors.primary};
    color: #000;
  }
`;

function App() {
  const [tasks, setTasks] = useLocalStorage<Task[]>('tasks', []);

  const [isHovering, setIsHovering] = useState(false);

  const handleMouseEnter = () => {
    setIsHovering(true);
  }

  const handleMouseLeave = () => {
    setIsHovering(true);
  }

  return (
    <>
      <GlobalStyle />
      <BrowserRouter>
        <TaskContext.Provider value={[tasks, setTasks]}>
          <div>
            <div className='logo'>
                <img height={60} width={60} src="logo.png" alt="logo" />
                <h1>Logical Loop</h1>
            </div>

          </div>
          <Layout>
            <div className='layout'>

            <div className='categories'>
              <div className='category'>
                <img height={80} width={80} src="data:image/jpeg;base64," alt="" />
              </div>
              <div className='category'>
                <img onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} style={{
                  border: isHovering ? '5px solid black' : ''

                }} height={80} width={80} src="https://i.pinimg.com/474x/5b/61/c5/5b61c584df554f546f9076ce166fde8d.jpg" alt="" />
              </div>
              <div className='category'>
                <img height={80} width={80} src="https://previews.123rf.com/images/frankrohde/frankrohde1104/frankrohde110403988/9370776-beautiful-illustrated-flower-background-design-with-gradient.jpg" alt="" />
              </div>
              <div className='category'>
                <img height={80} width={80} src="data:image/png;base64," alt="" />
              </div>
              <div className='category'>
                <img height={80} width={80} src="data:image/jpeg;base64," alt="" />
              </div>
            </div>

            <Nav>
              <TabButton exact to="/" activeClassName="active">
                List
              </TabButton>
              <TabButton to="/focus" activeClassName="active">
                Focus
              </TabButton>
            </Nav>

            <Switch>
              <Route exact path="/">
                <ListScreen />
              </Route>
              <Route path="/focus">
                <FocusScreen />
              </Route>
            </Switch>
            </div>
          </Layout>
        </TaskContext.Provider>
      </BrowserRouter>
    </>
  );
}

export default App;
