import { BrowserRouter, NavLink, Route, Switch } from 'react-router-dom';
import styled from 'styled-components';
import TaskContext from './contexts/task-store';
import useLocalStorage from './hooks/use-local-storage';
import FocusScreen from './screens/FocusScreen';
import ListScreen from './screens/ListScreen';
import { colors, GlobalStyle } from './styles';
import { Task } from './types';
import './app.css';
import {useState} from 'react';
import Category from './components/Category';


const Layout = styled.div`
  align-items: center;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  padding: 25px;
`;

const Nav = styled.nav`
  display: flex;
  margin-bottom: 45px;
`;

const TabButton = styled(NavLink)`
  align-items: center;
  background: #000;
  color: #fff;
  display: flex;
  height: 62px;
  justify-content: center;
  text-decoration: none;
  width: 120px;

  &:first-child {
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
  }

  &:last-child {
    border-top-right-radius: 15px;
    border-bottom-right-radius: 15px;
  }

  &.active {
    background: ${colors.primary};
    color: #000;
  }
`;

function App() {
  const [tasks, setTasks] = useLocalStorage<Task[]>('tasks', []);

  return (
    <>
      <GlobalStyle />
      <BrowserRouter>
        <TaskContext.Provider value={[tasks, setTasks]}>
          <div>
            <div className='logo'>
                <img height={60} width={60} src="logo.png" alt="logo" />
                Logical Loop
            </div>

          </div>
          <Layout>
            <div className='layout'>

            <div className='categories'>
              <Category src={'data:image/jpeg;base64,'} />
              <Category src={'https://i.pinimg.com/474x/5b/61/c5/5b61c584df554f546f9076ce166fde8d.jpg'} />
              <Category src={'https://previews.123rf.com/images/frankrohde/frankrohde1104/frankrohde110403988/9370776-beautiful-illustrated-flower-background-design-with-gradient.jpg'} />
              <Category src={'https://thumbs.dreamstime.com/z/beautiful-butterfly-design-17200443.jpg'} />
              <Category src='https://image.shutterstock.com/image-illustration/3d-picture-golden-tree-white-260nw-2158251121.jpg' />
            </div>
        <div>
            <Nav>
              <TabButton exact to="/" activeClassName="active">
                List
              </TabButton>
              <TabButton to="/focus" activeClassName="active">
                Focus
              </TabButton>
            </Nav>

            <Switch>
              <Route exact path="/">
                <ListScreen />
              </Route>
              <Route path="/focus">
                <FocusScreen />
              </Route>
            </Switch>
        </div>
            </div>
          </Layout>
        </TaskContext.Provider>
      </BrowserRouter>
    </>
  );
}

export default App;
